func (s testSigner) Sign(digest []byte) [65]byte{
	sig, _ := crypto.Sign(digest, s.privkey)
	var sigbyte [65]byte
	copy(sigbyte[:], sig[:65])
	return sigbyte
}

func (s testSigner) Pubkey() []byte {
	publicKeyBytes := crypto.FromECDSAPub(&s.privkey.PublicKey)
	return publicKeyBytes
}


func (s testSigner) Address() ethcommon.Address {
	return crypto.PubkeyToAddress(s.privkey.PublicKey)
}

func (s *SignerSuite) SetUpTest(c *C) {
	// The following privkey has address 0xE80B6467863EbF8865092544f441da8fD3cF6074
	privateKey, err := crypto.HexToECDSA(TSS_TEST_PRIVKEY)
	// Uncomment the following code to generate new random private key pairs
	//privateKey, err := crypto.GenerateKey()
	//privkeyBytes := crypto.FromECDSA(privateKey)
	//c.Logf("privatekey %s", hex.EncodeToString(privkeyBytes))
	c.Assert(err, IsNil)
	tss := testSigner {
		privkey: privateKey,
	}
	metaContractAddress := ethcommon.HexToAddress(META_TEST_GOERLI_ADDRESS)
	signer, err := NewSigner(common.Chain("ETH"), GOERLI_RPC_ENDPOINT, tss.Address(), tss, META_TEST_GOERLI_ABI, metaContractAddress)
	c.Assert(err, IsNil)
	c.Logf("TSS Address %s", tss.Address().Hex())
	s.signer = signer

}

func (s *SignerSuite) TestSign(c *C) {
	data := []byte("1234")
	tx, sig, hash, err := s.signer.Sign(data, s.signer.tssSigner.Address(), 109, big.NewInt(2))
	_ = tx
	c.Assert(err, IsNil)
	pubkey, err := crypto.Ecrecover(hash, sig)
	c.Assert(err, IsNil)
	c.Assert(bytes.Equal(pubkey,s.signer.tssSigner.Pubkey()), Equals, true)
}

func (s *SignerSuite) TestMint(c *C) {
	txhash, err := s.signer.MMint(big.NewInt(1234), s.signer.metaContractAddress, 80000, []byte{})
	c.Assert(err, IsNil)
	c.Logf("txhash %s", txhash)
}